	function loadingIcon(icon){
		if(!icon){
			return defaultIcons.default;
		}
		if(icon.indexOf("base64:")!=-1){
			return icon;
		}
		const localIcon = defaultIcons[icon];
		return localIcon ? localIcon : "https://static.staticj.top/img/pics/svg/" + icon + ".svg"
	}
	
	function CommonTools(){
		// Ëé∑ÂèñÂÖÉÁ¥†styleÂ±ûÊÄß, ÂåÖÊã¨css‰∏≠ÁöÑ
		this.getStyleByElement = function(e, styleProp) {
		    if (window.getComputedStyle) {
		        return document.defaultView.getComputedStyle(e, null).getPropertyValue(styleProp);
		    } else if (e.currentStyle) {
		        return e.currentStyle[styleProp];
		    }
		};
		
		// ËÆ°ÁÆóÂÖÉÁ¥†Âú®ÊñáÊ°£(È°µÈù¢)‰∏≠ÁöÑÁªùÂØπ‰ΩçÁΩÆ
		this.getElementPosition = function(e){
		    return {
		        top: e.getBoundingClientRect().top + window.scrollY,        // ÂÖÉÁ¥†È°∂ÈÉ®Áõ∏ÂØπ‰∫éÊñáÊ°£È°∂ÈÉ®Ë∑ùÁ¶ª
		        bottom: e.getBoundingClientRect().bottom + window.scrollY,  // ÂÖÉÁ¥†Â∫ïÈÉ®Áõ∏ÂØπ‰∫éÊñáÊ°£È°∂ÈÉ®Ë∑ùÁ¶ª
		        left: e.getBoundingClientRect().left + window.scrollX,      // ÂÖÉÁ¥†Â∑¶ËæπÁõ∏ÂØπ‰∫éÊñáÊ°£Â∑¶‰æßË∑ùÁ¶ª
		        right: e.getBoundingClientRect().right + window.scrollX     // ÂÖÉÁ¥†Âè≥ËæπÁõ∏ÂØπ‰∫éÊñáÊ°£Â∑¶‰æßË∑ùÁ¶ª
		    };
		};
		
		// Ëé∑ÂèñÂèØËßÜÁ™óÂè£Âú®ÊñáÊ°£(È°µÈù¢)‰∏≠ÁöÑÁªùÂØπ‰ΩçÁΩÆ
		this.getWindowPosition = function(){
		    return {
		        top: window.scrollY,
		        bottom: window.scrollY + window.innerHeight,
		        left: window.scrollX,
		        right: window.scrollX + window.innerWidth
		    };
		};
		
		// Âà§Êñ≠ÂÖÉÁ¥†Âú®ÊñáÊ°£(È°µÈù¢)‰∏≠ÊòØÂê¶ÂèØËßÅ
		this.isVisualOnPage = function(ele){
		    if (this.getStyleByElement(ele, 'display') == 'none'
		        || this.getStyleByElement(ele, 'visibility') == 'hidden'
		        || this.getStyleByElement(ele, 'opacity') == '0') {
		        return false;
		    }
		    if (this.getStyleByElement(ele, 'position') != 'fixed'
		        && ele.offsetParent == null) {
		        return false;
		    }
		    var elePos = this.getElementPosition(ele);
		    if (elePos.bottom - elePos.top == 0 || elePos.right - elePos.left == 0
		        || elePos.bottom <= 0 || elePos.right <= 0) {
		        return false;
		    }
		    return true;
		};
		
		// Ëé∑ÂèñÈÄâ‰∏≠ÊñáÊú¨
		this.getSelection = function(){
			return window.getSelection().toString().trim();
		};
		
		this.loadSheet = function(sheet, id){
			if(!id){
				id = "default-"+Math.ceil(Math.random()*100000000);
			}
			const css = document.createElement('style');
			css.type = "text/css";
			css.id = id;
			css.textContent = sheet;
			const heads = document.getElementsByTagName('head');
			if(heads && heads.length>0){
				document.getElementsByTagName('head')[0].appendChild(css);
			}else{
				document.body.insertAdjacentHTML('beforeend', html);
			}
		};
		
		this.getPlatform = function(){
			const userAgent = navigator.userAgent.toLocaleLowerCase();
			let platform = "unknown";
			if(userAgent.indexOf("mac")!=-1){
				platform = "mac";
			}else{
				platform = "win";
			}
			return platform;
		};
		
		/**
		 * @param {Object} time
		 * @param {Object} format
		 * Êó∂Èó¥Ê†ºÂºèÂåñ
		 * DateFormat(new Date(dateCreated), "yyyy-MM-dd hh:mm:ss")
		 */
		this.dateFormat = function(time, format) {
			if(!time){time=new Date();}
			var o = {
				"M+": time.getMonth() + 1, //Êúà‰ªΩ 
				"d+": time.getDate(), //Êó• 
				"h+": time.getHours(), //Â∞èÊó∂ 
				"m+": time.getMinutes(), //ÂàÜ 
				"s+": time.getSeconds(), //Áßí 
				"q+": Math.floor((time.getMonth() + 3) / 3), //Â≠£Â∫¶ 
				"S": time.getMilliseconds() //ÊØ´Áßí 
			};
			if(/(y+)/.test(format)){
				format = format.replace(RegExp.$1, (time.getFullYear() + "").substr(4 - RegExp.$1.length));
			}
			for(var k in o){
				if(new RegExp("(" + k + ")").test(format)){
					format = format.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
				}
			}
			return format;
		};
	}
	
